import base64

from fontTools.ttLib import TTFont, BytesIO


def make_ront_file(base64_string: str):
    bin_data = base64.decodebytes(base64_string.encode())
    with open('testotf1.woff', "wb") as f:
        f.write(bin_data)
    return bin_data


def convert_font_to_xml(bin_data):
    font = TTFont(BytesIO(bin_data))
    font.saveXML('test1.xml')


base_str = ''
test_font = '龥龒龒'
if __name__ == '__main__':
    convert_font_to_xml(make_ront_file(base64_string=base_str))

